import "./App.css";
import React, { useState, useRef } from "react";
import pokedex from './stuff/pokedex.jpg'

import SearchQuery from "./components/searchQuery";

function App() {
  const [pokemon, setPokemon] = useState("");
  const [typedPokemon, setTypedPokemon] = useState('')

  const pokeRef = useRef();
  function handleSearch(event) {
    const query1 = pokeRef.current.value;
    if (query1.length === 0) {
      return;
    }
    setPokemon(query1);
  }

  const keyDown = (event) => {
    if (event.key === "Enter") {
      handleSearch(event);
    }
  };

  console.log(pokemon)
  return (
    <>
      <header>
        <h1>Pokédex</h1>
        <img src={pokedex} alt=" help us please" />
      </header>
      <form onSubmit={function (event) {
        event.preventDefault()
        setPokemon(typedPokemon)
      }}>
        <input
          id="inputId"
          type="text"
          ref={pokeRef}
          onKeyPress={keyDown}
          placeholder="insert pokémon here"
          onChange={function (event) {
            setTypedPokemon(event.target.value)
          }}
        />
        <button id="submitButton">
          Go!
        </button>
        <SearchQuery pokemon={pokemon} />
      </form>
    </>
  );
}

export default App;
