import "./App.css";
import React, { useState, useRef } from "react";

import SearchQuery from "./components/searchQuery";

function App() {
  const [pokemon, setPokemon] = useState("");

  const pokeRef = useRef();
  function handleSearch(event) {
    const query1 = pokeRef.current.value;
    if (query1.length === 0) {
      return;
    }
    setPokemon(query1);
  }

  const keyDown = (event) => {
    if (event.key === "Enter") {
      handleSearch(event);
    }
  };

  return (
    <>
      <header>
        <h1>Pokedex</h1>
      </header>
      <input
        id="inputId"
        type="text"
        ref={pokeRef}
        onKeyPress={keyDown}
        placeholder="insert pokemon here"
      />
      <button id="submitButton" onClick={handleSearch}>
        Go!
      </button>
      <SearchQuery query={pokemon} setPokemon={setPokemon} />
    </>
  );
}

export default App;
