import "./App.css";
import React, { useState, useRef } from "react";

import SearchQuery from "./components/searchQuery";

function App() {
  const [pokemon, setPokemon] = useState("");
  const [typedPokemon, setTypedPokemon] = useState('')

  const pokeRef = useRef();
  function handleSearch(event) {
    const query1 = pokeRef.current.value;
    if (query1.length === 0) {
      return;
    }
    setPokemon(query1);
  }

  const keyDown = (event) => {
    if (event.key === "Enter") {
      handleSearch(event);
    }
  };


  console.log(typedPokemon)
  return (
    <>
      <header>
        <h1>Pokedex</h1>
      </header>
      <form onSubmit={function (e) {
        e.preventDefault()
        setPokemon()
      }}>
        <input
          id="inputId"
          type="text"
          ref={pokeRef}
          onKeyPress={keyDown}
          placeholder="insert pokemon here"
          onChange={function (e) {
            setTypedPokemon(e.target.value)
          }}
        />
        <button id="submitButton" onClick={handleSearch}>
          Go!
        </button>
        <SearchQuery query={pokemon} setPokemon={setPokemon} />
      </form>
    </>
  );
}

export default App;
