import "./App.css";
import React, { useState, useRef } from "react";
import pokedex from "./stuff/pokedex.jpg";

import SearchQuery from "./components/searchQuery";
import Header from "./components/header";

function App() {
  const [pokemon, setPokemon] = useState("");
  const [typedPokemon, setTypedPokemon] = useState("");

  const pokeRef = useRef();
  function handleSearch(event) {
    const query1 = pokeRef.current.value.toLowerCase();
    if (query1.length === 0) {
      return;
    }
    setPokemon(query1);
  }

  const keyDown = (event) => {
    if (event.key === "Enter") {
      handleSearch(event);
    }
  };

  console.log(pokemon);
  return (
    <>
      <header>
        <br></br>
        <br></br>
        <Header />
        <h1>Pokédex</h1>
        <img src={pokedex} alt=" help us please" width="199" height="172" />
      </header>
      <body>
        <form
          onSubmit={function (event) {
            event.preventDefault();
            setPokemon(typedPokemon);
          }}
        >
          <input
            id="inputId"
            type="text"
            ref={pokeRef}
            onKeyPress={keyDown}
            placeholder="Insert Pokémon here"
            onChange={function (event) {
              setTypedPokemon(event.target.value);
            }}
          />
          <button id="submitButton">Go!</button>
          <SearchQuery pokemon={pokemon} />
        </form>
      </body>
    </>
  );
}

export default App;
